import React, { useState } from 'react';

export const SignUpForm = () => {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');

  const handleEmailChange = (e) => {
    if (e.target.includes('@')){
      setEmail(e.target.value);
      document.querySelector(`#EmailError`).innerHTML = ""
    }
    else{
      document.querySelector(`#EmailError`).innerHTML = "Email must include @"
    }
    
    

  };
  const handlefNameChange = (e) => {
    if (e.target.value != ' '){
      setFirstName(e.target.value)
      document.querySelector(`#fNameError`).innerHTML = ""
    }
    else{
      document.querySelector(`#fNameError`).innerHTML = "First Name must not be blank."
    }
    
    
    
  };

  const clearForm = () => { 
     setFirstName(''); 
     setLastName(''); 
     setEmail(''); 
  };
  const submitForm = () => {
    setFirstName('')
  }

  return (
    <div className="container">
      <div>
        <label className="form-label">First Name</label>
        <input
            type="text"
            value={firstName}
            onChange={handlefNameChange}
        />
        <p id="fNameError">

        </p>
      </div>
      <div>
        <label className="form-label">Last Name</label>
        <input
            type="text"
            value={lastName}
            onChange={(event) => setLastName(event.target.value)}
        />
        <p id="lNameError">

        </p>
      </div>
      <div>
        <label className="form-label">Email</label>
        <input type="email" value={email} onChange={handleEmailChange} />
        <p id="emailError">

        </p>
      </div>
      
      
      
      <button onClick={clearForm} className="button">
          Clear
      </button>
      <button onClick={submitForm} className="button">
          Submit
      </button>
    </div>
  );
};
